#include &lt;iostream&gt;

using namespace std;

/**
 * Tinh so Fibonacci thu n
 * 
 * @param n: chi so cua so Fibonacci tinh tu 0 
 *           vd: F0 = 0, F1 = 1, F2 = 1, F3 = 2
 * @return So Fibonacci thu n
 */
int fibonacci(int n) {
    int f0 = 0;
    int f1 = 1;
    int fn = 1;
    int i;

    if (n &lt; 0) {
        return -1;
    } else if (n == 0 || n == 1) {
        return n;
    } else {
        for (i = 2; i &lt; n; i++) {
            f0 = f1;
            f1 = fn;
            fn = f0 + f1;
        }
    }
    return fn;
}

/**
 * Ham main
 */
int main() {
 int i;
    cout &lt;&lt; "10 so dau tien cua day so Fibonacci: \n";
    for (i = 0; i &lt; 10; i++) {
        cout &lt;&lt; fibonacci(i) &lt;&lt; " ";
    }
}

