INSERT OR REPLACE INTO song (id,name,artist,album,genre,lang,year,date,publishedAt,link_ytb,mp3,avatar,lyrics) VALUES ('mat-moc-vanh','mặt mộc','VAnh','Mặt Mộc','pop','vi','2023','2023-07-06','2022-08-17T12:00:11Z','https://www.youtube.com/watch?v=sc3L19mKUWs','https://drive.google.com/uc?export=download&id=15FVzN1c81EOgQbHqWE08ED5AwCx3_nfo','https://i.pinimg.com/564x/3c/ec/88/3cec886b8bdf0fc2d5d849c1a4a95383.jpg','Không cần mang giày cao gót không cần phấn son cầu kì\nMà khiến anh nằm mơ em đêm ngày\nBên cạnh em bình yên quá anh chỉ muốn say mãi trong làn tóc thơm dịu dàng\nHay là anh gặp ba má xin được rước em về nhà\nĐể giữ em của riêng anh cả đời\nĐợi chờ anh đã lâu biết không em muốn bên người\nChẳng tìm đâu ra một người như thế\nMôi cười xinh như hoa đáng yêu thật thà\nCứ êm đềm thương nhau qua bao nắng mưa\nMột đời nâng niu một đời đưa đón\nKhi bình minh ôm em ngủ say trong lòng\nĐến khi già nua vẫn say đắm em thôi\nAnh mang tâm tình gửi trọn vẹn vào từng lời ca\nNgượng ngùng nên khó nói ra cô gái của anh em hiểu mà\nVới anh em là niềm vui\nVới anh em là tất cả\nLà chén rượu ngọt ngào nhất mà anh may mắn được thử qua\nAnh muốn được mang nụ cười em về cất ở trong nhà\nÁo kia sứt chỉ sờn vai có một người giúp anh chuyện may vá\nDẫu anh không không đẹp vẫn có người gọi anh là soái ca\nLàm khùng làm điên rồi hai đứa nhìn nhau khà khà\nAnh đùa ấy\nAnh xin thế là anh đang nghiêm túc\nNghiêm túc muốn được vì em cả đời cố gắng mà không chịu khuất phục\nSẽ luôn là người đàn ông bên em khi vui hay khi em cáu\nĐợi đi về đâu cũng được miễn là hai đứa đi cùng nhau\nEm đồng ý em nhá\nMai anh qua thưa chuyện với mẹ cha\nBạn bè cô bác hai bên dắt tay hai đứa về chung một nhà\nTrời nắng hay trời mưa\nGiữa trưa hay trời sập tối\nTrái tim này một lòng một dạ sẽ mãi chỉ hướng về em thôi\nNhưng em tặng anh cả thế giới anh lại nói anh chẳng mong gì\nChỉ muốn một vườn hoa sau sân nhà\nHay tặng em đời anh nhé đêm ngày luôn săn sóc nuông chiều lắng nghe mọi điều\nHay là anh gặp ba má nói rằng lỡ thương quá em rồi\nVì nhớ nhung ngày đêm anh bơ phờ\nTình yêu anh giản đơn đã thương thương đến vô bờ\nChẳng tìm đâu ra một người như thế\nMôi cười xinh như hoa đáng yêu thật thà\nCứ êm đềm thương nhau qua bao nắng mưa\nMột đời nâng niu một đời đưa đón\nKhi bình minh ôm em ngủ say trong lòng\nĐến khi già nua vẫn say đắm em thôi\nNgười đẹp như mây một đời anh say\nAnh nguyện đưa em đi khắp nơi trên đời\nNgắm mây ngàn giăng giăng trên núi cao\nCuộc đời cho em thật nhiều may mắn\nKhi chạm môi anh em ngỡ như thiên đàng\nĐến khi già nua vẫn hôn mãi anh thôi\nĐến khi già nua vẫn say đắm em thôi');
