INSERT OR REPLACE INTO song (id,name,artist,album,genre,lang,year,date,publishedAt,link_ytb,mp3,avatar,lyrics) VALUES ('phai-chang-em-a-yeu-juky-san','phải chăng em đã yêu','juky san','Symphonies Juky San No.22','pop','vi','2021','2023-07-06','2021-02-12T12:00:15Z','https://www.youtube.com/watch?v=O81_4VAson4','https://firebasestorage.googleapis.com/v0/b/carrotstore.appspot.com/o/song%2Faudio%2Fmpeg%2F1688613379951_ph%E1%BA%A3i%20ch%C4%83ng%20em%20%C4%91%C3%A3%20y%C3%AAu.mp3?alt=media&token=f2ac7a98-4062-462b-bbbb-9a2a209f8ae4','https://firebasestorage.googleapis.com/v0/b/carrotstore.appspot.com/o/song%2Fimage%2Fjpeg%2F1688613365837_ph%E1%BA%A3i%20ch%C4%83ng%20em%20%C4%91%C3%A3%20y%C3%AAu.jpg?alt=media&token=91480d0b-7d8d-483c-b9e3-732bd8a7f06b','Phải chăng em đã yêu ngay từ cái nhìn đầu tiên\nPhải chăng em đã say ngay từ lúc thấy nụ cười ấy\nTình yêu ta ngất ngây xây được chín tầng trời mây khuất xa mờ\nÁnh lên từng giấc mơ ngày có anh\nCuộc đời em vốn chỉ là đường thẳng mà thôi\nMà sao tình cờ gặp anh em rẽ ngang qua đời\nVài người vội vã vội đến rồi đi\nMà sao em yêu anh đâu cần nghĩ suy\nTừng đêm nhớ mong về người\nBiết anh còn chờ đợi\nChơi vơi bao đêm em thấy đủ rồi\nNgàn tia nắng anh gần lại\nÁnh dương màu mắt xanh ngời\nLoé lên ngàn giấc mơ còn trong đời\nPhải chăng em đã yêu ngay từ cái nhìn đầu tiên\nPhải chăng em đã say ngay từ lúc thấy nụ cười ấy\nTình yêu ta ngất ngây xây được chín tầng trời mây khuất xa mờ\nÁnh lên từng giấc mơ ngày có anh\nTrái đất vốn lạ thường\nMà sao em cứ đi nhầm đường\nLạc vào tim anh lẻ loi\nĐằng sau chữ yêu đây là thương\nWhen you call me a baby make me so crazy\nMy heart is breaking slowly\nChầm chậm bờ môi khẽ trôi\nÔi mình yêu thật rồi\nTình cờ biết nhớ những lúc ngây thơ (nhớ ghê)\nLắm lúc chỉ biết vu vơ\nBơ vơ lạc trong những giấc mơ\nLắm lúc chỉ muốn nói anh mãi thương em\nNhưng trong tim bâng khuâng chẳng có ai xem này\nVì chữ thương nặng lắm đâu thể phơi bày\nPhải chăng em đã yêu ngay từ cái nhìn đầu tiên (my baby girl)\nPhải chăng em đã say ngay từ lúc thấy nụ cười ấy\nTình yêu ta ngất ngây xây được chín tầng trời mây khuất xa mờ\nÁnh lên từng giấc mơ ngày có anh\nYêu hay không yêu\nThương em anh hãy nói\nTrao nhau đôi môi\nRồi sẽ trở thành đôi\nEm đang chơi vơi\nLiệu anh có bước tới\nChầm chậm nói đôi lời khiến em chợt vui cười\nPhải chăng em đã yêu ngay từ cái nhìn đầu tiên\nPhải chăng em đã say ngay từ lúc thấy nụ cười ấy\nTình yêu ta ngất ngây xây được chín tầng trời mây khuất xa mờ\nÁnh lên từng giấc mơ ngày có anh');
