INSERT OR REPLACE INTO song (id,name,artist,album,genre,lang,year,date,publishedAt,link_ytb,mp3,avatar,lyrics) VALUES ('chac-anh-co-noi-kho-tam-thieu-bao-tram','chắc anh có nỗi khổ tâm','thiều bảo trâm','Thiều Bảo Trâm Official','pop','vi','2023','2023-08-24','2023-08-20T12:00:09Z','https://www.youtube.com/watch?v=mwj-R9EY2GI','https://firebasestorage.googleapis.com/v0/b/carrotstore.appspot.com/o/song%2Faudio%2Fmpeg%2F1692874034050_chac_anh_co_kho_tam.mp3?alt=media&token=e158cc7a-995b-4391-a2f5-d3203869feef','https://firebasestorage.googleapis.com/v0/b/carrotstore.appspot.com/o/song%2Fimage%2Fjpeg%2F1692873960204_chac_anh_co_kho_tam.jpg?alt=media&token=c01342bc-989a-45f0-9ba7-89769ee3b4d1','Ta có mọi thứ sao chẳng có nhau\nEm tìm hạnh phúc, anh trao đớn đau\nChắc anh có nỗi khổ tâm, tất cả chỉ là hiểu lầm thôi, phải không?\nGặp nhau là duyên nhưng chẳng có phận\nTừng nghĩ rằng yêu đương không hối hận\nChắc có lẽ là em sai, tính quá sớm chuyện tương lai rồi\n[Chorus]\nMình đừng vòng vo thêm nữa, đừng hứa, đừng tổn thương nhau thêm\nCũng đừng quan tâm về em, cảm xúc em ra sao cảm nhận thế nào, thì sao nào?\nMình cũng chẳng còn gì để nói với nhau\nVậy giờ mình dừng lại thôi, đừng khóc, mình chẳng có tương lai\nNên đừng bận tâm rằng ai là người đúng - sai\nCả hai đứa đều khờ dại, tất cả để trời xanh an bài\nCó khi nào sau này, sau này, sau này ta gặp gỡ lại\nKhi cả hai đã đủ lớn, đủ trưởng thành, đã biết đúng sai\nLiệu ta có nói lời cảm ơn nhau, hay ta chỉ toàn trách móc nhau?\nTiếc thay là những gì qua rồi qua rồi qua rồi chẳng thể trở lại\nBởi chuyện tình yêu là do trời an bài, chẳng phải bởi ai\nChấp nhận đi thôi, tất cả lỗi lầm do ông trời');
