INSERT OR REPLACE INTO song (id,name,artist,album,genre,lang,year,date,publishedAt,link_ytb,mp3,avatar,lyrics) VALUES ('mong-the-gioi-diu-dang-voi-em-ngoc-dolil','mong thế giới dịu dàng với em','Ngọc Dolil','Ngọc Dolil','pop','vi','2023','2023-12-29','2023-12-28T12:00:09Z','https://www.youtube.com/watch?v=iyP1kGWisuk','https://firebasestorage.googleapis.com/v0/b/carrotstore.appspot.com/o/song%2Faudio%2Fmpeg%2F1703829002882_Dolil.mp3?alt=media&token=8be3eb37-7224-4267-a22e-7bbb0577c267','https://firebasestorage.googleapis.com/v0/b/carrotstore.appspot.com/o/song%2Fimage%2Fjpeg%2F1703828982343_Dolil.jpg?alt=media&token=6f35e52d-a314-498b-af6a-0f187f312df2','v1:&nbsp;\nLê bàn chân nặng nề\nchậm chạp rời khỏi nơi đầy ắp bộn bề\nlách len giữa phố xa đông người\nmà trái tim trống rỗng, lẻ loi\ncon đường đã bao lần\nmà ngày hôm nay bỗng đằng đẵng, chông chênh\nkìm lòng cũng đã không biết bao nhiêu lần\nmà giờ cớ sao nước mắt cứ rơi\nchorus: buồn thì cứ khóc, mặc bao ánh mắt\nthả nỗi buồn kia cuốn theo mây trời\ngồng mình suốt bao lâu rồi, mình đã vất vả nhiều rồi\nbuồn thì cứ khóc cho vơi hết ấm ức ngày qua\nngày mai sẽ khác, trời quang nắng hát\nvà em xứng đáng có được tháng ngày an yên\ndù đời đôi lúc hay bất ngờ, chẳng như mong chờ\nvẫn mong ngoài đấy thế giới sẽ chở che cho em và luôn dịu dàng với em\nv2:&nbsp;\nhôm nay ra sao, tâm tư như nao sao em lẳng lặng không nói\nsâu trong tim em, còn điều gì buồn, xin người hãy nói đừng cố\nmạnh mẽ ôm lấy bao phiền muộn, không muốn thấy em phải buồn, không muốn nước mắt em lại tuôn\nbaby em hãy nhớ rằng là nhánh cỏ dại vẫn nở hoa ở nơi gỗ đá\nbaby em hãy cứ mở lòng chớ tuyệt vọng còn nhiều người thương em &amp; lo lắng mà\nbaby em hãy cứ vô tư là em, hãy giữ trên môi của em nụ cười nhưng nếu muốn thì cứ khóc cho thỏa lòng\nchorus: buồn thì cứ khóc, mặc bao ánh mắt\nthả nỗi buồn kia cuốn theo mây trời\ngồng mình suốt bao lâu rồi, mình đã vất vả nhiều rồi\nbuồn thì cứ khóc cho vơi hết ấm ức ngày qua\nngày mai sẽ khác, trời quang n