INSERT OR REPLACE INTO song (id,name,artist,album,genre,lang,year,date,publishedAt,link_ytb,mp3,avatar,lyrics) VALUES ('cau-duyen-hong-duyen','cầu duyên','hồng duyên','Hồng Duyên Official','pop','vi','2024','2024-02-01','2024-01-25T13:00:07Z','https://www.youtube.com/watch?v=tA_fWKV679M','https://firebasestorage.googleapis.com/v0/b/carrotstore.appspot.com/o/song%2Faudio%2Fmpeg%2F1706720676589_cau_duyen.mp3?alt=media&token=b04f42f3-d13e-4cc9-b811-16c0848030ab','https://firebasestorage.googleapis.com/v0/b/carrotstore.appspot.com/o/song%2Fimage%2Fjpeg%2F1706720670378_cau_duyen.jpg?alt=media&token=e7561c9e-ac6f-476d-98c0-df65f9f6f792','Đông tàn xuân sang cánh én lượn bay về\nEm thời xuân mơn má hồng đào thơm\nMang một nỗi lòng duyên tình long đong em chưa lấy chồng\nMẹ cha ngóng trông hỏi thăm mỗi năm bao giờ lấy chồng?!\nChorus:\nNắng mưa là chuyện của trời\nMột duyên hai nợ người ơi\nTrăm năm trọn một chữ tình,\nCó duyên có nợ có tình có đôi\nCầu đầu xuân năm mới có duyên tương phùng\nCầu tơ duyên đưa lối có anh đi cùng\nMình sẽ sống với nhau bạc đầu anh ơi\nNgười sẽ nắm bàn tay em mình cùng nhau bước qua bao bão giông cuộc đời\nTựa như trăng với sao có nhau chẳng tách rời...');
INSERT OR REPLACE INTO song (id,name,artist,album,genre,lang,year,date,publishedAt,link_ytb,mp3,avatar,lyrics) VALUES ('tuoi-mong-xu-ong-hien-ngan','tuổi mộng xứ đông','hiền ngân','Hiền Ngân Official','pop','vi','2022','2024-02-01','2022-04-07T12:00:11Z','https://www.youtube.com/watch?v=gfEst1-InMg','https://firebasestorage.googleapis.com/v0/b/carrotstore.appspot.com/o/song%2Faudio%2Fmpeg%2F1706721716356_tuoi_mong_xu_dong.mp3?alt=media&token=02e02cef-5860-4aa5-88e7-875ecf561df6','https://firebasestorage.googleapis.com/v0/b/carrotstore.appspot.com/o/song%2Fimage%2Fjpeg%2F1706721710651_tuoi_mong_xu_dong.jpg?alt=media&token=9f9128a5-5958-4af4-b8b2-3ac36c60d9f5','Anh hỡi anh khi trời mưa lất phất trên phố\nThương nhớ ai cho nỗi sầu vẫn rụng xuống vai\nTừng giọt mưa, mưa rơi giữa cơn mộng cánh chim nồng say\nTình yêu là sợi tơ đó thấy chưa.\nAnh biết không ông trời vẫn ghét người hồng nhan\nThương nhớ ai cho khúc nhạc vẫn còn dở dang\nChiều hôm nay khi nghe tiếng chim lảnh lót trong vườn cây\nEm vẫn mong phố đông người sánh bước bên chàng.\nĐK:\nMưa ướt tóc mây, bóng tối đường dài\nHoa trắng mới lay, gió đưa hương chẳng phai\nNgày mai dấu yêu hãy đưa em vào câu chuyện huyền thoại\nTình đôi ta mong giờ đây duyên thắm đẹp đôi.\nAnh hỡi anh mai này có tính chuyện trầu cau\nXin nhớ cho em gái còn tuổi mộng xứ đông\nChiều hôm nay mưa rơi trái tim nhỏ bé như chờ mong\nAi đứng trông mắt môi nàng thủa thắm xuân nồng.');
