INSERT OR REPLACE INTO song (id,name,artist,album,genre,lang,year,date,publishedAt,link_ytb,mp3,avatar,lyrics) VALUES ('bien-tinh-may-bae-cover','biển tình','mây bae cover','Mây Bae','pop','vi','2024','2024-07-27','2024-05-02T12:00:08Z','https://www.youtube.com/watch?v=4XrqDJfJzz4','https://firebasestorage.googleapis.com/v0/b/carrotstore.appspot.com/o/song%2Faudio%2Fmpeg%2F1722016605869_bien_tinh.mp3?alt=media&token=c31d8a0d-598c-4004-a10b-c99052f09064','https://firebasestorage.googleapis.com/v0/b/carrotstore.appspot.com/o/song%2Fimage%2Fjpeg%2F1722016673914_bien_tinh.jpg?alt=media&token=da646491-8fb8-4759-b805-8f87241eb2b2','Nằm nghe sóng vỗ từng lớp xa\nBọt tràn theo từng làn gió đưa\nMột vầng trăng sáng với tình yêu chúng ta\nVượt ngàn hải lý cũng không xa\nBiển rộng đất trời chỉ có ta\nThì giòng ngân hà mình cũng qua\nBiển không biên giới, như tình anh với em\nHơn cả những vì sao đêm...\nTrăng nhô lên cao, trăng gác trên đầu núi\nMây xanh xanh lơ vì đắm say tình mới\nĐến đây với em mà ngỡ trong giấc mơ\nMắt em âu sầu là cả một trời thơ\nKhông gian im nghe nhịp đôi tim hẹn ước\nMong sao tương lai đường trăng ta cùng bước\nXiết tay dắt nhau mình lánh xa thế nhân\nLánh xa ưu phiền đắng cay trần gian\nĐời anh sẽ đẹp vì có em\nNgày dài sẽ làm mình nhớ thêm\nBiển xanh cát trắng, sóng hòa nhịp ái ân\nKhông còn những chiều bâng khuâng');
INSERT OR REPLACE INTO song (id,name,artist,album,genre,lang,year,date,publishedAt,link_ytb,mp3,avatar,lyrics) VALUES ('trai-at-om-mat-troi-kai-dinh','trái đất ôm mặt trời','kai dinh','Kai Dinh Official','pop','vi','2023','2024-07-27','2023-12-18T13:00:11Z','https://www.youtube.com/watch?v=ohgodOTSHjA','https://firebasestorage.googleapis.com/v0/b/carrotstore.appspot.com/o/song%2Faudio%2Fmpeg%2F1722016869806_trai_dat_om_mat_troi.mp3?alt=media&token=4fb4a026-2540-44b2-8a55-e1cfdb50fb28','https://firebasestorage.googleapis.com/v0/b/carrotstore.appspot.com/o/song%2Fimage%2Fjpeg%2F1722016820900_trai_dat_om_mat_troi.jpg?alt=media&token=837e0a05-184d-4df6-adc6-1fa6b5d6d2d7','Anh cứ ôm bao hi vọng nhìn về phía em\nDù nơi chúng ta là ngày với đêm\nTình yêu ấy vô vọng như nỗi nhớ dịu êm\nTrái đất nhìn về mặt trời\nTrái tim anh cũng như mọi vì tinh tú\nCứ thế xoay mãi theo vũ trụ\nĐuổi theo ánh sáng của mặt trời mà cứ xa vời suốt đời\nCho anh được biết vì sao giam bí mật của em\nNơi em sẽ đến mỗi khi nỗi cô đơn hiện lên\nBài hát mà em muốn nghe để thấy được vỗ về\nBài hát sẽ ôm em, ôm em, ôm em, ôm em\nÝ nghĩa của những ngôi sao lấp lánh trên cao, em biết không?\nÝ nghĩa của trái tim anh đang đập từng giây, em hiểu không?\nTình yêu mà anh dành cho em, tình yêu mà không cần gọi tên\n愛してる（愛してる）\nAnh cứ ôm bao hi vọng nhìn về phía em\nDù nơi chúng ta là ngày với đêm\nTình yêu ấy vô vọng như nỗi nhớ dịu êm\nTrái đất nhìn về mặt trời\nTrái tim anh cũng như mọi vì tinh tú\nCứ thế xoay mãi theo vũ trụ\nĐuổi theo ánh sáng của mặt trời mà cứ xa vời suốt đời\nYou''re the sun, you''re the sun, you''re the sun, the sun\nYou''re the sun, you''re the sun, you''re the sun, the sun\nYou''re the sun, you''re the sun, the sun, the sun, the sun\nOoh, ooh-oh ooh-oh ooh-oh\nMột điều em chôn giấu, hãy nói anh biết đi\nĐể anh giữ làm kho báu, để cho anh đỡ suy\nBài hát em từng yêu, người em từng xiêu lòng\nNhững điều em thầm mong và đôi tay em mơ được nắm một đời\nDù là một ngàn năm ánh sáng, anh vẫn chờ, babe, I''m waiting on the phone này\nNếu buồn hơn, I''m asking, "Are you okay?"\nNếu như không phiền thì để anh tập bay xuyên thế giới\nỞ cạnh người nhìn thời gian trôi\nAnh cứ ôm bao hi vọng nhìn về phía em\nDù nơi chúng ta là ngày với đêm\nTình yêu ấy vô vọng như nỗi nhớ dịu êm\nTrái đất nhìn về mặt trời\nTrái tim anh cũng như mọi vì tinh tú (chỉ là vì tinh tú)\nCứ thế xoay mãi theo vũ trụ\nĐuổi theo ánh sáng của Mặt trời mà cứ xa vời suốt đời\nYou''re the sun, the sun, the sun\nYou''re the sun, the sun, the sun\nYou''re the sun, the sun, the sun, the sun\nTrái tim anh chỉ là, chỉ là một vì tinh tú thôi\nYou''re the sun, the sun, the sun\nYou''re the sun, the sun, the sun, the sun\n愛してる、愛してる');
INSERT OR REPLACE INTO song (id,name,artist,album,genre,lang,year,date,publishedAt,link_ytb,mp3,avatar,lyrics) VALUES ('ung-lam-trai-tim-anh-au-son-tung','đừng làm trái tim anh đau','son tung','Sơn Tùng M-TP Official','pop','vi','2024','2024-07-27','2024-06-08T13:00:01Z','https://www.youtube.com/watch?v=abPmZCZZrFA','https://firebasestorage.googleapis.com/v0/b/carrotstore.appspot.com/o/song%2Faudio%2Fmpeg%2F1722017062648_dung_lam_trai_tim_anh_dau.mp3?alt=media&token=8bee5bba-ecaa-4318-b2a2-4c8f2008adf0','https://firebasestorage.googleapis.com/v0/b/carrotstore.appspot.com/o/song%2Fimage%2Fjpeg%2F1722017015850_dung_lam_trai_tim_anh_dau.jpg?alt=media&token=abf11065-a798-4499-b3f2-ab39b89ff441','Hình như trong lòng anh đã không còn hình bóng ai ngoài em đâu\nHằng đêm anh nằm thao thức suy tư, chẳng nhớ ai ngoài em đâu\nVậy nên không cần nói nữa, yêu mà đòi nói trong vài ba câu\nCứ cố quá đâm ra lại hâm\nUhm, đau hết cả đầu\nĐợI chờ em trước nhà từ sáng đến trưa chiều tối mắc màn đây luôn (Ah-ah-ah-ah)\nNgược nắng hay là ngược gió, miễn anh thấy em tươi vui không buồn\nChỉ cần có thấy thế thôi mây xanh chan hoà\n(Uhm) thấy thế thôi vui hơn có quà\nVà bước kế tiếp anh lại gần hơn chút đó nha\nRồi ngày ấy cuối cùng đã tìm đến, ta nào đâu hay (Hay)\nAnh sẽ không để vụt mất đi cơ duyên ông trời trao tay\nCòn đắn đo băn khoăn gì nữa, tiếp cận em ngay (Ngay)\nCố gắng sao không để em nghi ngờ dù một giây lúc này\nĐượC đứng bên em anh hạnh phúc, tim loạn nhịp tung bay (Bay)\nChắc chắn anh thề anh sẽ không bao giờ quên ngày hôm nay\nChính em, chính em, tương tư mình em thôi\nMãi theo sau mình em thôi, mãi si mê mình em thôi\n(Mãi yêu thương mình em)\nVậy thì anh xin chết vì người anh thương\nCó biết bao nhiêu điều còn đang vấn vương\nDành cho em, dành hết ân tình anh mang một đời\nĐừNg làm trái tim anh đau\nVậy thì anh xin chết vì người anh thương\nNên có biết bao nhiêu điều còn đang vấn vương\nDành cho em, dành hết ân tình anh mang một đời\nĐừNg làm trái tim anh đau\nTình cờ lọt